targets.expr:
sum by(response_result, response_code) (
  count_over_time(
    {filename="/logs/logs.log"} 
    |~ `\[INFO\]|\[ERROR\]` 
    | pattern `<_> - <json_content>` 
    | json response_result="additionalInfo.responseResult", response_code="additionalInfo.responseCode" 
    | response_result != "" 
    | response_code != "" [$__range]
  )
)

count by(filename) (
  count_over_time(
    {filename="/logs/logs.log"} 
    |~ `\[INFO\]|\[ERROR\]` 
    | pattern `<_> - <json_content>` 
    | json response_code="additionalInfo.responseCode" 
    | response_code != "" [$__range]
  )
)
